implementation.javascript:
function zeitreihenMittelwert(daten) {
    return daten.reduce((a, b) => a + b, 0) / daten.length;
}

implementation.rust:
fn zeitreihen_mittelwert(daten: &[f64]) -> f64 {
    daten.iter().sum::<f64>() / daten.len() as f64
}

implementation.go:
func zeitreihenMittelwert(daten []float64) float64 {
    sum := 0.0
    for _, v := range daten {
        sum += v
    }
    return sum / float64(len(daten))
}

implementation.java:
public static double zeitreihenMittelwert(double[] daten) {
    return Arrays.stream(daten).sum() / daten.length;
}

implementation.cpp:
double zeitreihenMittelwert(vector<double>& daten) {
    double sum = accumulate(daten.begin(), daten.end(), 0.0);
    return sum / daten.size();
}

implementation.csharp:
public static double ZeitreihenMittelwert(double[] daten) {
    return daten.Sum() / daten.Length;
}

implementation.swift:
func zeitreihenMittelwert(daten: [Double]) -> Double {
    return daten.reduce(0, +) / Double(daten.count)
}

implementation.kotlin:
fun zeitreihenMittelwert(daten: DoubleArray): Double {
    return daten.average()
}

implementation.typescript:
